import Typography from '../atoms/Typography';
import GreetingAnimation from '../molecules/GreetingAnimation';
import { TextSwap, ContactRedirect } from '../../utils/hooks';
import Contact from '../compounds/Contact';

const mainClass = 'main';
const greetingClass = `${mainClass}__greeting`;

const Main = props => {
  return (
    <>
      <main {...props}>
        <TextSwap>
          {({ mounted }) => (
            <>
              <GreetingAnimation mounted={mounted} />
              {mounted === 3 && (
                <div className="contact-personalImg">
                  <ContactRedirect>
                    {({ openTab }) => (
                      <Contact
                        openTab={openTab}
                        style={{ margin: '52px 10px' }}
                        direction="column"
                      />
                    )}
                  </ContactRedirect>
                  <img
                    className="contact-personalImg__img"
                    src={
                      'https://res.cloudinary.com/paulkim/image/upload/v1552394092/images/misc/family-cropped.jpg'
                    }
                  />
                </div>
              )}
            </>
          )}
        </TextSwap>
      </main>
      <style jsx>{`
        main {
          height: 400px;
          width: 600px;
          background-image: url('https://images.unsplash.com/photo-1488923937092-5a9ccc6eff59');
          background-repeat: no-repeat;
          background-size: contain;
          background-blend-mode: multiply;
          background-color: rgba(0, 0, 0, 0.4);
        }
        .contact-personalImg {
        }

        .contact-personalImg__img {
          height: 250px;
        }
      `}</style>
    </>
  );
};

export default Main;
