import Typography from '../atoms/Typography';
import GreetingAnimation from '../molecules/GreetingAnimation';
import { TextSwap } from '../../utils/hooks';

const mainClass = 'main';
const greetingClass = `${mainClass}__greeting`;

const Main = props => {
  return (
    <>
      <main {...props}>
        <TextSwap>
          {({ mounted }) => <GreetingAnimation mounted={mounted} />}
        </TextSwap>
      </main>
      <style jsx>{`
        main {
          height: 500px;
          width 58.5%;
          background-image: url('https://images.unsplash.com/photo-1488923937092-5a9ccc6eff59');
          background-repeat: no-repeat;
          background-size: contain;
          background-blend-mode: multiply;
          background-color: rgba(0, 0, 0, 0.3);
        }
        div {
          font-size: 30px;
        }
        img {
          height: 400px;
          width: 600px;
        }
      `}</style>
    </>
  );
};

export default Main;
